import { useState } from "react";

const Example = () => {
    const [val, setVal] = useState(0)
    const countUp = ()=>{
        // 次回のレンダリング
        setVal(val + 1)
    }
    const countDown = () =>{
        setVal(val - 1)
    }
    return(
    <>
        <p>現在のカウント数:{val}</p>
        <button onClick={countUp}>+</button>
        <button onClick={countDown}>-</button>
    </>
    )
};

export default Example;
