import {useState} from 'react'

const Example = () => {
  const [value , setValue] = useState('')
  return (
    <>
      <label htmlFor='123'>ラベル</label>
      <div>
        <input
          id="123"
          placeholder="こんにちは"
          value={value}
          onChange = {(e)=> setValue(e.target.value)}
        />

      </div>
    </>
  );
};

export default Example;
