import {useState} from 'react'

const Example = () => {
  const [click , setClick] = useState(false)
  const toggleChange = (prevState) =>{
    setClick((e)=>{
      let state = !e
      return state
    })
  }
  return (
    <>
    <label>チェック :
      <input
        type="checkbox"
        checked={click}
        onclick={toggleChange}
      />
    </label>
    <p>{click}</p>
    </>
  );
};

export default Example;
