import Profile from './components/Profile'

const Example = () => {
  const users = [
    {name: 'Geo', age: '18', hobby:['sports','music']},
    {name: 'Tom', age: '25', hobby:['movie','music']},
    {name: 'Lisa', age: '21', hobby:['sports','travel','game']}
  ]
  const newUsers = users.map(user => <Profile name={user.name} age={user.age} hobby={user.hobby}/>)
  return (
    <>
      <h3>練習問題</h3>
      <p>Profileコンポーネントを使用して、完成コードと同じ画面を作成してください。</p>
      <p>また、Profileコンポーネント内のリスト表示部分にkeyを設定して、ワーニング表示がされないようにしてください。</p>
      <ul>
        {newUsers}
      </ul>
    </>
  );
};

export default Example;
