import {useState} from 'react'


const Form = ({newTodosList}) =>{
    const [value,setValue] = useState("")
    const addArray = ()=>{
        newTodosList.push(value)
    }
    return(
        <>
        <input type="text" onChange={(e)=>{setValue(e.target.value)}}/>
        <input type="button" onClick={addArray} value={"追加"}/>
        </>
    )
}

export default Form;
