import {useState} from 'react'

const Example = () => {
  const [value , setValue] = useState('')
  return (
    <>
    <label></label>
    <input
      id="123"
      placeholder="こんにちは"
      value={value}
      onChange = {(e)=> setValue(e.target.value)}
    />
    </>
  );
};

export default Example;
