import {useState} from 'react'

const Example = () => {
  const [fruit, setFruit] = useState('')
  const onChange = (e)=> setFruit(e.target.value)
  return (
    <>
      <input
        type="radio"
        value="apple"
        checked = {fruit === "apple"}
        onChange={onChange}
      />
      Apple
    </>
  );
};

export default Example;
