import { VStack,StackDivider, HStack } from "@chakra-ui/react";

const List = ({todos, deleteTodo}) => {
    const complete = (id) => {
        deleteTodo(id)
    }
    return (
        <VStack
        divider={<StackDivider/>}
        borderColor='red.200'
        p={5}
        >
            {todos.map(todo => {
                return (
                    <HStack key={todo.id} spacing={3}>
                        <button onClick={() => complete(todo.id)}>完了</button>
                        <span>{todo.content}</span>
                    </HStack>
                )
            })}
        </VStack>
    );
}

export default List;
