import { useState } from "react";
import styled from "styled-components";

const FirstButton = styled.button`
  display: inline-block;
  border-radius: 3px;
  padding: 0.5rem 0;
  margin: 0.5rem 1rem;
  width: 11rem;
  border: none;
  background: pink;
`;

const SecondButton = styled(FirstButton)`
background: red;
color: white;
`
const ThirdButton = styled(SecondButton)`
background: ${({color}) => color ? 'black': 'green'};
`


const Example = () => {
  const [color, colorSet] = useState(true)

  const ChangeColor = ()=>{
    colorSet(color => !color)
  }
  return (
    <>
      <h3>練習問題</h3>
      <p>
        記述を変更し、完成コードと同じ状態になるようにしてください。
        <ul>
          <li>Q1. FirstButtonのbackgroudをpinkにしてください。</li>
          <li>
            Q2.
            FirstButtonを継承したSecondButtonを作成し、backgroudをredに、colorをwhiteにしてください。{" "}
          </li>
          <li>
            Q3.
            SecondButtonを継承したThirdButtonを作成し、props.darkがある場合のみbackgroudがblackに、ない場合はgreenになるようにしてください。
          </li>
        </ul>
      </p>
      <FirstButton>ボタン1</FirstButton>
      <SecondButton>ボタン2</SecondButton>
      <ThirdButton color={color} onClick={ChangeColor}>ボタン3</ThirdButton>
      <ThirdButton color={color} onClick={ChangeColor}>ボタン3</ThirdButton>
      {/* <ThirdButton dark={dark} >ボタン4</ThirdButton> */}
    </>
  );
};

export default Example;

// この状態で参照していたカラーがprops前のものだったので
